tinymce.PluginManager.add("own_checkbox", function (editor, f) {
/**
* Setup buttons for Bdesk Editor
* @lastchange: $arsalanshah
*/
   editor.addButton("own_checkbox", {
	  icon: 'image',
	  title: 'Add checkbox',
      cmd: "own_checkbox"
    });
/**
* Init the plugin
* @lastchange $arsalanshah
*/ 
   editor.addCommand("own_checkbox", function () {
	editor.execCommand("mceInsertRawHTML", false, "<input type=checkbox onclick='this.checked = true'>");
    });
/**
* Setup Insert buttons for Bdesk Editor Dialog
* @lastchange: $arsalanshah
*/ 
     editor.addMenuItem("own_checkbox", {
        cmd: "own_checkbox",
        context: "insert",
		text: 'Embed Image',
		icon: 'image',
    });
});
